mapper.update:
UPDATE PG_message
SET message_readcount = message_readcount+1
WHERE message_no= #{message_no}

UPDATE PG_message
SET message_senddelete = message_senddelete+1
WHERE message_no =#{message_no}

UPDATE PG_message
SET message_receivedelete = message_receivedelete+1
WHERE message_no =#{message_no}
